    def insert_user(self):
        try:
            # Establish Connection
            connection = mysql.connector.connect(
                host="localhost",
                user="root",
                password="HelloWorld@1234",
                database="handgesture"
            )

            cursor = connection.cursor()

            # Retrieve data from UI
            name = self.lineEdit.text()
            password = self.lineEdit_2.text()
            email = self.lineEdit_3.text()

            # Insert data into the database
            query = "INSERT INTO details (name, password, email) VALUES (%s, %s, %s)"
            cursor.execute(query, (name, password, email))
            connection.commit()

            print("User inserted successfully!")
            self.clear_fields()
            msg = QMessageBox()
            msg.setIcon(QMessageBox.Information)
            msg.setText("User added successfully!")
            msg.setWindowTitle("Success")
            msg.exec_()
            # Close the signup window after successful signup
            self.window.close()

        except mysql.connector.Error as error:
            print("Failed to connect to MySQL: {}".format(error))
            msg = QMessageBox()
            msg.setIcon(QMessageBox.Critical)
            msg.setText(f"Failed to add user: {error}")
            msg.setWindowTitle("Error")
            msg.exec_()
            print("Failed to connect to MySQL: {}".format(error))

        finally:
            if connection.is_connected():
                cursor.close()
                connection.close()
                print("MySQL connection is closed")
    def clear_fields(self):
        self.lineEdit.setText('')
        self.lineEdit_2.setText('')
        self.lineEdit_3.setText('')

